asayfa"];		$tarih = date("d.m.Y H:i");		$ekleyen = $_SESSION["uye_id"];		$etiket = explode(",",$_POST["etiket"]);		$etiketler = implode(",",$etiket);				$konuEkle = mysql_query("insert into konular (konu_basligi,konu_basligi_sef,konu_anasayfa,konu_icerik,etiketler,kategori_id,ekleyen,eklenme_tarihi,onay,anasayfa) values ('$baslik','$baslikSef','$anasayfa_icerik','$tam_icerik','$etiketler','$kategori','$ekleyen','$tarih','$onay','$anasayfa')");		if ($konuEkle){			$konuid = mysql_insert_id();			foreach ($etiket as $e){				$eSef = sef_link($e);				$etiketEkle = mysql_query("insert into etiketler (konu_id,etiket,etiket_sef) values ('$konuid','$e','$eSef')");			}			header("Location:index.php?git=konular");		}else {			hata("Bir Sorun Oluþtu","Konu Veritabanýna Eklenemedi");		}		}else {		?>		<form action="" method="post">		<div class="blok">Konu Baþlýðý:</div>		<div class="blok"><input type="text" name="baslik" class="admin_input" /></div>		<div class="blok">Kategori:</div>		<div class="blok"><select name="kategori" class="admin_select">		<?php		$katBul = mysql_query("select * from kategoriler");		while ($katGoster = mysql_fetch_array($katBul)){			echo '<option value="'.$katGoster["id"].'">'.$katGoster["kategori_adi"].'</option>';		}		?>		</select></div>		<div class="blok">Konu Anasayfa:</div>		<div class="blok"><textarea rows="0" cols="0" id="editor" name="anasayfa_icerik"></textarea></div>		<script type="text/javascript">		//<![CDATA[			// Replace the <textarea id="editor"> with an CKEditor			// instance, using default configurations.			CKEDITOR.replace( 'editor',				{					extraPlugins : 'uicolor',					uiColor: '#ddd',					toolbar :					[						[ 'Bold','-','Source', 'Italic', 'Font','FontSize', 'NumberedList', 'BulletedList', '-', 'Link', 'Unlink' ]					]				} );		//]]>		</script>		<div class="blok">Konu Ýçeriði:</div>		<div class="blok"><textarea rows="0" cols="0" id="editor2" name="tam_icerik"></textarea></div>		<script type="text/javascript">		//<![CDATA[			// Replace the <textarea id="editor"> with an CKEditor			// instance, using default configurations.			CKEDITOR.replace( 'editor2',				{					extraPlugins : 'uicolor',					uiColor: '#ddd',					toolbar :					[						[ 'Bold','-','Source', 'Italic', 'Font','FontSize', 'NumberedList', 'BulletedList', '-', 'Link', 'Unlink' ]					]				} );		//]]>		</script>		<div class="blok">Konu Onaylý mý?</div>		<div class="blok"><select name="onay" class="admin_select"><option value="1">Evet</option><option value="0">Hayýr</option></select></div>		<div class="blok">Anasayfada Gözüksün mü?</div>		<div class="blok"><select name="anasayfa" class="admin_select"><option value="1">Evet</option><option value="0">Hayýr</option></select></div>		<div class="blok">Konu Etiketleri:</div>		<div class="blok"><input type="text" name="etiket" class="admin_input" /></div>		<div class="blok"><input type="submit" value="Konu Ekle" class="giris_btn" /></div>		</form>		<?php		}}else {		$sayfa = $_GET["sayfa"];	if (!$sayfa || !is_numeric($sayfa)){$sayfa=1;}	$kacar = 5;	$ksayisi = mysql_num_rows(mysql_query("select * from konular"));	$ssayisi = ceil($ksayisi/$kacar);	$nereden = ($sayfa*$kacar)-$kacar;	$konuBul = mysql_query("select * from konular order by onay,id desc limit $nereden,$kacar");	while ($konuGoster = mysql_fetch_array($konuBul)){		echo '<div class="blok"><div style="float: right"><a style="background-color: red; color: #fff; padding: 2px" href="index.php?git=konular&duzenle='.$konuGoster["id"].'">Düzenle</a> <a style="background-color: red; color: #fff; padding: 2px" href="index.php?git=konular&sil='.$konuGoster["id"].'">Sil</a></div>'.$konuGoster["konu_basligi"].'</div>';	}	if ($ksayisi > $kacar){		echo '<div class="sayfala" style="margin-top: 10px">';		for ($i=1; $i<=$ssayisi; $i++){			echo '<a href="index.php?git=konular&sayfa='.$i.'"';			echo ($sayfa == $i ? ' class="aktif"' : '');			echo '>'.$i.'</a>'