<?phpif ($id = $_GET["duzenle"]){	if ($_POST){				$kadi = addslashes(trim($_POST["kadi"]));		$eposta = $_POST["eposta"];		$sifre= $_POST["sifre"];				if (!$kadi || !$eposta){			bilgi("Bir Sorun Var","Gerekli alanlarý doldurup öyle güncellemeyi deneyin!");		}else {								$guncelle = mysql_query("update uyeler set uye_kadi='$kadi', uye_eposta='$eposta' ,uye_sifre='$sifre' where uye_id='$id'");			if ($guncelle){				header("Location:index.php?git=uyeler");			}else {				bilgi("Bir Sorun Oluþtu","Üye güncellenemedi!!");			}				}			}else {				$uyeBul = mysql_query("select * from uyeler where uye_id='$id'");		$uyeSay = mysql_num_rows($uyeBul);				if ($uyeSay > 0){						$uyeGoster = mysql_fetch_array($uyeBul);			echo '<h1>Üye Düzenle</h1>';			echo '<form action="" method="post">			<div class="blok">Kullanýcý Adý:</div>			<div class="blok"><input type="text" name="kadi" value="'.$uyeGoster["uye_kadi"].'" class="admin_input" /></div>			<div class="blok">Þifre:</div>			<div class="blok"><input type="text" name="sifre" value="'.$uyeGoster["uye_sifre"].'" class="admin_input" /></div>			<div class="blok">E-Posta:</div>			<div class="blok"><input type="text" name="eposta" class="admin_input" value="'.$uyeGoster["uye_eposta"].'" /></div>																		<div class="blok"><input type="submit" value="Üye Düzenle" class="giris_btn" /></div>			</form>';					}else {			header("Location:index.php?git=uyeler");		}			}}elseif ($id = $_GET["sil"]){	$sil = mysql_query("delete from uyeler where uye_id='$id'");	if ($sil){		header("Location:index.php?git=uyeler");	}else {		bilgi("Bir Sorun Oluþtu","Üye silinemedi!!");	}}else {		echo '<h1>Üye Listesi</h1>';	$kacar = 15;	$ksayisi = mysql_num_rows(mysql_query("select id from uyeler"));	$ssayisi = ceil($ksayisi/$kacar);	$uyeBul = mysql_query("select * from uyeler");		$uyeSay = mysql_num_rows($uyeBul);		if ($uyeSay > 0){				while ($uyeGoster = mysql_fetch_array($uyeBul)){			echo '<div class="blok"';			echo '><div style="float: right"><a style="background-color: red; color: #fff; padding: 2px" href="index.php?git=uyeler&duzenle='.$uyeGoster["uye_id"].'">Düzenle</a> <a style="background-color: red; color: #fff; padding: 2px" href="index.php?git=uyeler&sil='.$uyeGoster["uye_id"].'">Sil</a></div>'.$uyeGoster["uye_kadi"].'</div>';		}				if ($kacar > $ksayisi){			for ($i = 1; $i < $ssayisi; $i++){				echo '<a href="index.php?git=konular&sayfa='.$i.'"';				echo ($sayfa == $i ? ' class="aktif"' : '');				echo '>'.$i.'</a>';			}		}			}else {		echo 'Henüz üye kayýtlý deðil!!';	}	}?>